function App() {
   return (
    <React.Fragment>
      <AuthContext.Provider value={{
        isLoggedIn:isLoggedIn,
        onLogout: logoutHandler
      }}>
      <MainHeader />
      <main>
        {!isLoggedIn && <Login onLogin={loginHandler} />}
        {isLoggedIn && <Home onLogout={logoutHandler} />}
      </main>
      </AuthContext.Provider>
    </React.Fragment>
  );
}

export default App;
